s06 = AirProp(T6,'T','s')DS4 = (s06-s04)-(0.287*log(P6/P4))s6 = s4 + DS4%Point 7P7 = RopPrt6 = AirProp(T6,'T','p')Prt7 = Prt6*(Rop/P6)hs7 = AirProp(Prt7,'p','h')h7 = h6-(T1i*(h6-hs7))T7 = AirProp(h7,'h','T')s07 = AirProp(T7,'T','s')DS5 = (s07-s06)-(0.287*log(P7/P6))s7 = s6 + DS5%Point 8P8 = RopT8 = Roth8 = AirProp(T8,'T','h')s08 = AirProp(T8,'T','s')DS6 = (s08-s07)-(0.287*log(P8/P7))s8 = s7 + DS6%Point 9P9 = P6/TprPrt9 = Prt6*(P9/Rop)hs9 = AirProp(Prt9,'p','h')h9 = h8-(T2i*(h8-hs9))T9 = AirProp(h9,'h','T')s09 = AirProp(T9,'T','s')DS7 = (s09-s08)-(0.287*log(P9/P8))s9 = s8 + DS7%Point 5P5 = Pcin*Cprh5 = (Re*(h9-h4))+h4T5 = AirProp(h5,'h','T')s05 = AirProp(T5,'T','s')DS8 = (s05-s04)-(0.287*log(P5/P4))s5 = s4 + DS8%Point 10P10 = P6/Tprh10 = ((1-Re)*(h9-h4))+h4T10 = AirProp(h10,'h','T')s010 = AirProp(T10,'T','s')DS9 = (s010-s09)-(0.287*log(P10/P9))s10 = s9 + DS9%Total Turbine WorkWt = (h6-h7)+(h8-h9)%Total Compressor WorkWc = (h2-h1)+(h4-h3)%Total Heat AddedQin = (h6-h5)+(h8-h7)%Thermal Efficiencyeff = (Wt-Wc)/Qin%Backwork Ratiobwr = Wc/Wt%Net Power DevelopedWcycle = m*(Wt-Wc)set (h.Total_Turbine_Work_edit, "string", num2str (Wt));set (h.Total_Compressor_Work_edit, "string", num2str (Wc));set (h.Total_Heat_Added_edit, "string", num2str (Qin));set (h.Thermal_Efficiency_edit, "string", num2str (eff));set (h.Backwork_Ratio_edit, "string", num2str (bwr));set (h.Net_Power_Developed_edit, "string", num2str (Wcycle));set (h.P1_edit, "string", num2str (P1));set (h.T1_edit, "string", num2str (T1));set (h.h1_edit, "string", num2str (h1));set (h.s1_edit, "string", num2str (s1));set (h.P2_edit, "string", num2str (P2));set (h.T2_edit, "string", num2str (T2));set (h.h2_edit, "string", num2str (h2));set (h.s2_edit, "string", num2str (s2));set (h.P3_edit, "string", num2str (P3));set (h.T3_edit, "string", num2str (T3));set (h.h3_edit, "string", num2str (h3));set (h.s3_edit, "string", num2str (s3));set (h.P4_edit, "string", num2str (P4));set (h.T4_edit, "string", num2str (T4));set (h.h4_edit, "string", num2str (h4));set (h.s4_edit, "string", num2str (s4));set (h.P5_edit, "string", num2str (P5));set (h.T5_edit, "string", num2str (T5));set (h.h5_edit, "string", num2str (h5));set (h.s5_edit, "string", num2str (s5));set (h.P6_edit, "string", num2str (P6));set (h.T6_edit, "string", num2str (T6));set (h.h6_edit, "string", num2str (h6));set (h.s6_edit, "string", num2str (s6));set (h.P7_edit, "string", num2str (P7));set (h.T7_edit, "string", num2str (T7));set (h.h7_edit, "string", num2str (h7));set (h.s7_edit, "string", num2str (s7));set (h.P8_edit, "string", num2str (P8));set (h.T8_edit, "string", num2str (T8));set (h.h8_edit, "string", num2str (h8));set (h.s8_edit, "string", num2str (s8));set (h.P9_edit, "string", num2str (P9));set (h.T9_edit, "string", num2str (T9));set (h.h9_edit, "string", num2str (h9));set (h.s9_edit, "string", num2str (s9));set (h.P10_edit, "string", num2str (P10));set (h.T10_edit, "string", num2str (T10));set (h.h10_edit, "string", num2str (h10));set (h.s10_edit, "string", num2str (s10));end%save plotfunction savedata(hObject, eventdata)h = guidata(hObject);Fig1 = figure('Units','pixels','Position',[50, 50, 450, 225]);set(Fig1,'Visible','off');newAxes = copyobj(h.sp4,Fig1);##filter = {'*.jpg';'*.png'};folder1 = fullfile('C:/Users','.png');[file, folder] = uiputfile(folder1);if isequal(file,0) || isequal(folder,0)   disp('User clicked Cancel.')else   disp(['User selected ',fullfile(folder,file),...         ' and then clicked Save.'])endfullFileName = fullfile(folder, file)saveas(Fig1,fullFileName);close(Fig1);end%clearfunction clearall(hObject,eventdata)h = guidata(hObject);set (h.Total_Turbine_Work_edit, "string", num2str (0));set (h.Total_Compressor_Work_edit, "string", num2str (0));set (h.Total_Heat_Added_edit, "string", num2str (0));set (h.Thermal_Efficiency_edit, "string", num2str (0));